
public class WhileLoopTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 1; 
		
		while(i <= 5 ) {
				System.out.println("**********************" + i);
				//i = i +1;
				i++;
		}
		i = 1; 
		while (i <= 5) {
			System.out.println(i);
			//i = i +1;
			i++;
		}
		
	}

}
